let parsedData = [];
let tableName = '';
let errorMessage = '';

try {
  // æ ¹æ“šæ–‡ä»¶é¡å‹è§£æ
  if (fileInfo.mimeType.includes('text/csv') || fileInfo.name.endsWith('.csv')) {
    // CSVæ–‡ä»¶è§£æ
    const csvContent = Buffer.from(fileData.data, 'base64').toString('utf-8');
    const lines = csvContent.split('\n').filter(line => line.trim());

    if (lines.length > 0) {
      const headers = lines[0].split(',').map(h => h.trim().replace(/"/g, ''));

      for (let i = 1; i < lines.length; i++) {
        const values = lines[i].split(',').map(v => v.trim().replace(/"/g, ''));
        const record = {};

        headers.forEach((header, index) => {
          record[header] = values[index] || null;
        });

        parsedData.push(record);
      }
    }

  } else if (fileInfo.mimeType.includes('application/json') || fileInfo.name.endsWith('.json')) {
    // JSONæ–‡ä»¶è§£æ
    const jsonContent = Buffer.from(fileData.data, 'base64').toString('utf-8');
    const jsonData = JSON.parse(jsonContent);

    if (Array.isArray(jsonData)) {
      parsedData = jsonData;
    } else if (typeof jsonData === 'object') {
      parsedData = [jsonData];
    }

  } else if (fileInfo.mimeType.includes('spreadsheet') || fileInfo.name.endsWith('.xlsx')) {
    // Excelæ–‡ä»¶è™•ç† (éœ€è¦é¡å¤–è™•ç†)
    errorMessage = 'Excelæ–‡ä»¶éœ€è¦é¡å¤–çš„è§£ææ­¥é©Ÿ';
  }

  // æ ¹æ“šæ–‡ä»¶åæ¨æ–·è¡¨å
  tableName = fileInfo.name
    .replace(/\.[^/.]+$/, '') // ç§»é™¤å‰¯æª”å
    .toLowerCase()
    .replace(/[^a-z0-9]/g, '_') // æ›¿æ›ç‰¹æ®Šå­—ç¬¦
    .replace(/_+/g, '_') // åˆä½µå¤šå€‹åº•ç·š
    .replace(/^_|_$/g, ''); // ç§»é™¤é–‹é ­çµå°¾çš„åº•ç·š

  // å¦‚æœè¡¨åç‚ºç©ºï¼Œä½¿ç”¨é è¨­åç¨±
  if (!tableName) {
    tableName = 'imported_data_' + Date.now();
  }

} catch (error) {
  errorMessage = error.message;
}

return {
