// å‹•æ…‹è¡¨çµæ§‹å‰µå»ºå™¨
const { tableName, allData, columns, fileInfo } = $json;

if (!allData || allData.length === 0) {
  throw new Error('æ²’æœ‰æ•¸æ“šå¯ä»¥å‰µå»ºè¡¨çµæ§‹');
}

// åˆ†æåˆ—é¡å‹
const columnDefinitions = [];
const sampleSize = Math.min(100, allData.length); // å–æ¨£100ç­†åˆ†æé¡å‹

columns.forEach(column => {
  const cleanColumnName = column
    .toLowerCase()
    .replace(/[^a-z0-9]/g, '_')
    .replace(/_+/g, '_')
    .replace(/^_|_$/g, '');

  // åˆ†ææ•¸æ“šé¡å‹
  let isNumeric = true;
  let isBoolean = true;
  let isDate = true;
  let maxLength = 0;

  for (let i = 0; i < sampleSize; i++) {
    const value = allData[i][column];

    if (value === null || value === undefined || value === '') continue;

    const strValue = String(value).trim();
    maxLength = Math.max(maxLength, strValue.length);

    // æª¢æŸ¥æ˜¯å¦ç‚ºæ•¸å­—
    if (isNumeric && isNaN(parseFloat(strValue))) {
      isNumeric = false;
    }

    // æª¢æŸ¥æ˜¯å¦ç‚ºå¸ƒæ—å€¼
    if (isBoolean && !['true', 'false', '1', '0', 'yes', 'no'].includes(strValue.toLowerCase())) {
      isBoolean = false;
    }

    // æª¢æŸ¥æ˜¯å¦ç‚ºæ—¥æœŸ
    if (isDate && isNaN(Date.parse(strValue))) {
      isDate = false;
    }
  }

  // æ±ºå®šæ•¸æ“šé¡å‹
  let dataType = 'TEXT';
  if (isBoolean && maxLength <= 10) {
    dataType = 'BOOLEAN';
  } else if (isNumeric) {
    dataType = 'DECIMAL';
  } else if (isDate) {
    dataType = 'TIMESTAMP WITH TIME ZONE';
  } else if (maxLength <= 255) {
    dataType = `VARCHAR(${Math.max(255, maxLength + 50)})`;
  }

  columnDefinitions.push({
    originalName: column,
    name: cleanColumnName || 'column_' + columnDefinitions.length,
    type: dataType,
    maxLength
  });
});

// ç”Ÿæˆå‰µå»ºè¡¨çš„SQL
const createTableSQL = `
CREATE TABLE IF NOT EXISTS ${tableName} (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  ${columnDefinitions.map(col => `${col.name} ${col.type}`).join(',\n  ')},

  -- å…ƒæ•¸æ“šæ¬„ä½
  source_file_id VARCHAR(255),
  source_file_name VARCHAR(500),
  import_timestamp TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  import_batch_id VARCHAR(100),

  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- å‰µå»ºç´¢å¼•
CREATE INDEX IF NOT EXISTS idx_${tableName}_source ON ${tableName}(source_file_id);
CREATE INDEX IF NOT EXISTS idx_${tableName}_import ON ${tableName}(import_timestamp);
`;

// æº–å‚™æ•¸æ“šæ’å…¥
const batchId = `batch_${Date.now()}`;
const insertData = allData.map(record => {
  const processedRecord = {
    source_file_id: fileInfo.id,
    source_file_name: fileInfo.name,
    import_batch_id: batchId
  };

  columnDefinitions.forEach(col => {
    let value = record[col.originalName];

    // æ•¸æ“šé¡å‹è½‰æ›
    if (value === null || value === undefined || value === '') {
      processedRecord[col.name] = null;
    } else if (col.type === 'BOOLEAN') {
      processedRecord[col.name] = ['true', '1', 'yes'].includes(String(value).toLowerCase());
    } else if (col.type === 'DECIMAL') {
      processedRecord[col.name] = parseFloat(value) || 0;
    } else if (col.type.includes('TIMESTAMP')) {
      processedRecord[col.name] = new Date(value).toISOString();
    } else {
      processedRecord[col.name] = String(value).trim();
    }
  });

  return processedRecord;
});

return {
  tableName,
  createTableSQL,
  insertData,
  batchId,
  columnDefinitions,
  recordCount: insertData.length,
  fileInfo
};

